import { GET_USERS, ALPHABETICAL_SORT, SEARCH_USER } from "./action-types";
import axios from 'axios';


export const getUsers = () => {
    return function(dispatch) {
        axios.get(`https://randomuser.me/api/?results=150`)
    .then((response) => {
        const users = response.data.results;
        return dispatch({type: GET_USERS, payload: response.data})
    }).catch((error) => {
        console.log(error)
    }
    )
}};

export function getUsersByName(payload) {
    return function(dispatch) {
        try {
            var response =  axios.get(`https://jsonplaceholder.typicode.com/users?name=${payload}`);
            return dispatch({type: SEARCH_USER, payload: response.data})
        } catch {
            return alert ('User Not Found')
        }
    }
}
