import { GET_USERS , GET_USERS_BY_ID  } from "./action-types";
import axios from 'axios';


export const getUsers = () => {
    return function(dispatch) {
      axios
        .get(`https://randomuser.me/api/?results=150`)
        .then((response) => {
          const users = response.data.results.map((user) => ({
            id: user.login.uuid,
            name: `${user.name.first} ${user.name.last}`,
            gender:user.gender,
            age: user.dob.age,
            image: user.picture.large,
            city: user.location.city,
            email: user.email,
            username:user.username,
            birthdate: user.dob.date,
            number:user.cell,
            email:user.email

          }));
          dispatch({ type: GET_USERS, payload: users });
        })
        .catch((error) => {
          console.log(error);
        });
    };
  };


  export const getUserById = (id) => {
    return function(dispatch, getState) {
      const user = getState().users.find((u) => u.id === id);
      if (user) {
        dispatch({ type: GET_USERS_BY_ID, payload: user });
      } else {
        axios
          .get(`https://randomuser.me/api/?seed=foobar&id=${id}`)
          .then((response) => {
            const user = response.data.results[0];
            const formattedUser = {
              id: user.login.uuid,
              name: `${user.name.first} ${user.name.last}`,
              gender:user.gender,
              age: user.dob.age,
              image: user.picture.large,
              city: user.location.city,
              email: user.email,
              username:user.username,
              birthdate: user.dob.date,
              number:user.cell,
              email:user.email
            };
            dispatch({ type: GET_USERS_BY_ID, payload: formattedUser });
          })
          .catch((error) => {
            console.log(error);
          });
      }
    };
  };
