import { GET_USERS, SEARCH_USER } from "./action-types";
import axios from 'axios';

export const getUsers = () => {
    return function(dispatch) {
      axios
        .get(`https://randomuser.me/api/?results=150`)
        .then((response) => {
          const users = response.data.results.map((user) => ({
            id: user.login.uuid,
            name: `${user.name.first} ${user.name.last}`,
            gender:user.gender,
            age: user.dob.age,
            image: user.picture.large,
            city: user.location.city,
            email: user.email,
            username:user.username,
            birthdate: user.dob.date,
            number:user.cell,
            email:user.email

          }));
          dispatch({ type: GET_USERS, payload: users });
        })
        .catch((error) => {
          console.log(error);
        });
    };
  };

  export function getUserById(id) {
    return async dispatch => {
      try {
        // Buscamos el usuario por ID en el estado local
        const user = getState().users.find(user => user.id === id);
        dispatch({ type: 'user/getUserById', payload: user });
      } catch (error) {
        console.log(error);
      }
    };
  }
