import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import doctor1 from '../../Assets/doctor1.jpg'
import doctor2 from '../../Assets/doctor2.png'
import doctor3 from '../../Assets/doctor3.png'
import doctor4 from '../../Assets/doctor4.avif'


const DetailDoctor = () => {
    const navigate = useNavigate();

  const { id } = useParams();

  const doctors = useSelector((state) => state.doctors);
const doctor = doctors.find((doctor) => doctor.id === Number(id));

  if (!doctor) {
    return <div>No se encontró el doctor</div>;
  }

 function handleBackClick() {
    navigate('/doctors');
  }

  const numVowels = (doctor.name.match(/a/gi) || []).length;
  ;

  return (
    <div>
      <h2>Doctor:</h2>

      <div className='doctorDetail'>
        <button className='closeDetail' onClick={handleBackClick}>x</button>
      <ul>
     
      {numVowels >= 3 && <img src={doctor1} alt="Imagen con 3 vocales" width="150" height="150"/>}
      {numVowels === 2 && <img src={doctor2} alt="Imagen con 2 vocales" width="150" height="150"/>}
      {numVowels === 1 && <img src={doctor3} alt="Imagen con 1 vocal" width="150" height="150"/>}
      {numVowels === 0 && <img src={doctor4} alt="Imagen sin vocales" width="150" height="150"/>}

        <p>Nombre: {doctor.name}</p>
        <p>Usuario: {doctor.user}</p>
        <p>E-mail:{doctor.email}</p>
        <p>Teléfono: {doctor.phone}</p>
        <p>Dirección: {doctor.suite} {doctor.street}, {doctor.address}</p>
        <p>Especialidad: {doctor.speciality}</p>
        <p>N° de matrícula: {doctor.registration}</p>
      </ul>
      </div>
    </div>
  );
};
/**
{
    "id": 1,
    "name": "Leanne Graham",
    "speciality":"neurologist",
    "username": "Bret",
    "email": "Sincere@april.biz",
    "address": {
      "street": "Kulas Light",
      "suite": "Apt. 556",
      "city": "Gwenborough"
    },
    "phone": 1770736803156442,
    "registration": "A234DD27"
  },
  
  */

export default DetailDoctor;
