import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Navigate } from "react-router-dom";
import { createUser } from "../../Redux/actions";
import { Link } from "react-router-dom";

///VALIDACIONES :)
const validate = (form) => {
    let errors = {};
    
   
    if (!form.name) {
        errors.name = 'Por favor ingresa un nombre';
    }
    else if (!form.lastname) {
        errors.lastname = 'Por favor ingresa un apellido';
    }

    else if (!form.user) {
        errors.user = 'Por favor genere un nombre de usuario';
    }

    else if (!form.email) {
        errors.email = 'Por favor ingresa un email';
    }
    else if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(form.email)) {
        errors.email = "Ingresa un email válido"
    }

    else if (!form.password) {
        errors.password = 'Por favor ingresa una contraseña';
    }
    else if (!/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*]).{8,}$/.test(form.password)) {
        errors.password = "La contraseña debe de tener mínimo 8 caracteres, al menos un número, una letra minúscula, una letra mayúscula y un carácter especial."
    }
    else if (form.password !== form.passwordconfirm) {
        errors.passwordconfirm = "Las contraseñas no coinciden"
    }
   
    return errors;
}


///FORMULARIO :)
const Form = () => {

    const dispatch = useDispatch();
   
    const [errors, setErrors] = useState({})

    const [form, setForm] = useState({
        role:"",
        speciality:"",
        credentials:"",
        name:"",
        lastname:"",
        user:"",
        email:"",
        password:"",
        passwordconfirm:"",
        image:"",
        age:"",
        birthday:"",
        gender:"",
        adress:"",
        phone:"",
        detection:""
      });
    
    function handleChange(e) {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
      
        setErrors(
          validate({
            ...form,
            [e.target.name]: e.target.value,
          })
        );
      }

      function handleDoctor(e) {
        const optionSelected = e.target.value;
        if (optionSelected === "medico") {
          document.getElementById("medicalInputs").style.display = "block";
        } else {
          document.getElementById("medicalInputs").style.display = "none";
        }
        setForm({
          ...form,
          role: optionSelected,
        });
      }

      function handleImageChange (e) {
        const file = e.target.files[0];
        const reader = new FileReader();
      
        reader.onloadend = () => {
          setForm({ ...form, image: reader.result });
        };
      
        if (file) {
          reader.readAsDataURL(file);
        }
      };

      function handleSubmit(e) {
        e.preventDefault();
        dispatch(createUser(form));
        alert("¡Tu usuario ha sido creado!");
        setForm({
            role:"",
            speciality:"",
            credentials:"",
            name:"",
            lastname:"",
            user:"",
            email:"",
            password:"",
            image:"",
            age:"",
            birthday:"",
            gender:"",
            adress:"",
            phone:"",
            detection:""
        });
        Navigate.push("/home");
      }


    return (
        <div className={styles.container}>
            <div className={styles.form}>
                <Link to={`/home`}>
                    <button className={styles.button1}> Volver </button>
                </Link>  
                <div>
                    <h3 className={styles.title}>INGRESA TUS DATOS</h3>
                    <form onSubmit={(e) => handleSubmit(e)}>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Rol: </label>
                            <select className={styles.select} value={form.role} id="optionSelected" onChange={(e) => handleDoctor(e)}>
                                <option disabled="">Selecciona una opción</option>
                                <option value="medico">Médico</option>
                                <option value="paciente">Paciente</option>
                            </select>
                            <p className={styles.error}>{errors.role}</p>
                        </div >
                            <div id="medicalInputs" className={styles.itemsContainer}>
                                <div>
                                    <label className={styles.label}>Especialidad: </label>
                                    <p>
                                    <input  className={styles.input} type="text" name="speciality" value={form.speciality} onChange={(e) => handleChange(e)} placeholder="Especialidad"/>
                                    <p>{errors.speciality}</p>
                                    </p>
                                </div>
                                <div>
                                    <label className={styles.label}>Credenciales: </label>
                                    <p>
                                    <input  className={styles.input} type="text" name="credentials" value={form.credentials} onChange={(e) => handleChange(e)} placeholder="Crdenciales"/>
                                    <p>{errors.credentials}</p>
                                    </p>
                                </div>
                            </div>
                    

                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Nombre: </label>
                            <p>
                            <input  className={styles.input} type="text" name="name" value={form.name} onChange={(e) => handleChange(e)} placeholder="Nombre"/>
                            <p className={styles.error}>{errors.name}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Apellido: </label>
                            <p>
                            <input  className={styles.input} type="text" name="lastname" value={form.lastname} onChange={(e) => handleChange(e)} placeholder="Apellido"/>
                            <p className={styles.error}>{errors.lastname}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Usuario: </label>
                            <p>
                            <input  className={styles.input} type="text" name="user" value={form.user} onChange={(e) => handleChange(e)} placeholder="Usuario"/>
                            <p className={styles.error}>{errors.user}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Email: </label>
                            <p>
                            <input  className={styles.input} type="text" name="email" value={form.email} onChange={(e) => handleChange(e)} placeholder="Email"/>
                            <p className={styles.error}>{errors.email}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Contraseña: </label>
                            <p>
                            <input  className={styles.input} type="password" name="password" value={form.password} onChange={(e) => handleChange(e)} placeholder="Contraseña"/>
                            <p className={styles.error}>{errors.password}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Confirma tu contraseña: </label>
                            <p>
                            <input  className={styles.input} type="password" name="passwordconfirm" placeholder="Contraseña" value={form.passwordconfirm} onChange={(e) => handleChange(e)}/>
                            <p className={styles.error}>{errors.passwordconfirm}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Imagen: </label>
                            <p>
                            <input  className={styles.image} type="file" name="image" accept="image/*" onChange={(e) => handleImageChange(e)} />
                            <p className={styles.error}>{errors.image}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Edad: </label>
                            <p>
                            <input  className={styles.input} type="number" min="1" max="100" step="1" name="age" value={form.age} onChange={(e) => handleChange(e)} placeholder="Edad"/>
                            <p>{errors.age}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Género: </label>
                            <p>
                            <input className={styles.check} type='checkbox' name="gender" value={form.gender} onChange={(e) => handleChange(e)} /> Femenino
                            <input className={styles.check} type='checkbox' name="gender" value={form.gender} onChange={(e) => handleChange(e)} /> Masculino
                            <p>{errors.genre}</p>
                            </p>
                        </div>
                        <p></p>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Fecha de nacimiento: </label>
                            <p>
                            <input  className={styles.input} type="date" name="birthday" value={form.birthday} onChange={(e) => handleChange(e)}/>
                            <p>{errors.birthday}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Dirección: </label>
                            <p>
                            <input  className={styles.input} type="text" name="adress" value={form.adress} onChange={(e) => handleChange(e)} placeholder="Dirección"/>
                            <p>{errors.adress}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Teléfono: </label>
                            <p>
                            <input  className={styles.input} type="text" name="phone" value={form.phone} onChange={(e) => handleChange(e)} placeholder="Teléfono"/>
                            <p>{errors.phone}</p>
                            </p>
                        </div>
                        <div className={styles.itemsContainer}>
                            <label className={styles.label}>Detección: </label>
                            <p>
                            <input  className={styles.input} type="text" name="detection" value={form.detection} onChange={(e) => handleChange(e)} placeholder="Detección"/>
                            <p>{errors.detection}</p>
                            </p>
                        </div>
                    
                        <div>
                            <button className={styles.button2} type="submit" disabled={ !form.role || !form.name || !form.user || !form.email }>CREAR USUARIO</button>
                        </div>
                    </form>
                </div>
            </div>   
        </div>
    )
}

export default Form;
