import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getUsers } from "../Redux/actions";
import User from './User';

export default function Home() {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);
  const [showUsers, setShowUsers]= useState(false);

  const handleShowUsers = () =>{
    setShowUsers(true);
  }

  const handleHideUsers = () => {
    setShowUsers(false);
  };

  const [orderType, setOrderType] = useState("ASC");

  const sortedUsers = users.sort((a, b) => {
    if (orderType === "ASC") {
      return a.name.localeCompare(b.name);
    } else {
      return b.name.localeCompare(a.name);
    }
  });



  useEffect(() => {
    dispatch(getUsers());
  }, [dispatch]);

  return (
    <div>
      <h1>Bienvenido al home </h1>
      {!showUsers && (
        <button onClick={handleShowUsers}>Mostrar usuarios</button>
      )}

      {showUsers && (
        <>
          <button onClick={handleHideUsers}>Ocultar usuarios</button>
          {users.map((user) => (
            <User key={user.id} login={user.login} />
          ))}
        </>
      )}

<button onClick={() => setOrderType(orderType === "ASC" ? "DESC" : "ASC")}>
        Ordenar por nombre {orderType === "ASC" ? "ascendente" : "descendente"}
      </button>
      {sortedUsers.map((user) => (
        <User key={user.id} name={user.login} />
      ))}

    </div>
  );
}
