import { GET_USERS,GET_USER_DETAILS} from "./action-types"


const initialState = {
    users : [],
    searched:'',
    userDetails: null
}


const reducer = (state = initialState, action) =>{
    switch(action.type){
        case GET_USERS:
            return {
                ...state,
                users: action.payload
            }

        case GET_USER_DETAILS:
            return{
                ...state,
                users:action.payload
            }

        default:
            return {...state}
    
        }
}

export default reducer;
