import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import style from "./NavBar.module.css"
import { useNavigate } from 'react-router-dom';

const NavBar = () => {
  const navigate = useNavigate();
  const [showMenu, setShowMenu] = useState(false);

  const handleShowUsers = () => {
    navigate('/users');
  }

  const handleShowDoctors = () => {
    navigate('/doctors');
  }

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  }

  return (
    <nav className={style.navbar}>
      <div className={style.container}>
        <Link to="/about" className={style.about}>About</Link>

        <div className={style.dropdownContainer}>
        <Link to="/" className={style.link} onClick={(e) => {e.preventDefault(); toggleMenu()}}>
  Nuestros usuarios
</Link>
          {showMenu && (
            <ul className={style.dropdownMenu}>
              <li className={style.users} onClick={handleShowUsers}>Pacientes</li>
              <li className={style.doctors} onClick={handleShowDoctors}>Doctores</li>
            </ul>
          )}
        </div>

        <Link to="/form" className={style.btn}>
          <button className={style.login}>LOG IN</button>
        </Link>
      </div>
    </nav>
  )
}

export default NavBar;
